import React from "react";
import {Box, IconButton} from "@material-ui/core";
import {Add, Remove, Delete} from "@material-ui/icons";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import ProductService from "../services/ProductService";
import {useHistory} from "react-router-dom";

export default function RecordInCart(props: any) {
    const [product, setProduct] = React.useState(props.quantity);
    let history = useHistory();

    const handleAdd = () => {
        setProduct(product + 1);

        ProductService.getProducts().then((products) => {
            for (let prod of products){
                if (prod.id === props.id){
                    console.log("incremented " + props.name);
                    document.cookie = `${props.id}=${props.name}, ${product}, ${props.price}; path=/`
                }
            }
        })

    }

    const handleSubtract = () => {
        setProduct(product - 1);
    }

    const handleDelete = () => {
        ProductService.getProducts().then((products) => {
            for (let prod of products){
                if (prod.id === props.id){
                    console.log("deleted " + props.name);
                    document.cookie = `${props.id}=${props.name}, ${product}, ${props.price}; path=/`
                        +";expires=" + new Date(0).toUTCString();

                    history.go(0);
                }
            }
        })
    }

    return (
        <TableRow>
            <TableCell>{props.name}</TableCell>
            <TableCell>{product}</TableCell>
            <TableCell>{props.price * product} z≈Ç</TableCell>
            <TableCell>
                <IconButton onClick={handleAdd} color="primary" aria-label="add to shopping cart">
                <Add/>
                </IconButton>
            </TableCell>
            <TableCell>
                <IconButton onClick={handleSubtract} color="primary" aria-label="add to shopping cart">
                    <Remove/>
                </IconButton>
            </TableCell>
            <TableCell>
                <IconButton onClick={handleDelete} color="primary" aria-label="add to shopping cart">
                    <Delete/>
                </IconButton>
            </TableCell>
        </TableRow>


        // <div>
        //     <Box display="flex"
        //          justifyContent="space-evenly">
        //         <h3>{props.name}</h3>
        //         <h3>{product}</h3>
        //         <h3>{props.price}</h3>
        //
        //         <IconButton onClick={handleAdd} color="primary" aria-label="add to shopping cart">
        //             <Add/>
        //         </IconButton>
        //         <IconButton onClick={handleSubtract} aria-label="remove shopping cart" color="secondary">
        //             <Remove/>
        //         </IconButton>
        //         <IconButton aria-label="delete" color="secondary">
        //             <Delete/>
        //         </IconButton>
        //     </Box>
        //
        // </div>
    );
}
