import React from "react";
import {Box, IconButton} from "@material-ui/core";
import {Add, Remove, Delete} from "@material-ui/icons";


export default function RecordInCart(props: any) {
    console.log("HENLO?")
    console.log("pepega " + props)

    const [product, setProduct] = React.useState(props);

    const handleAdd = () => {
        props.quantity += 1;
    }

    const handleSubtract = () => {
        props.quantity--;
    }

    return (
        <div>
            <Box display="flex"
                 justifyContent="space-evenly">
                <h3>{props.name}</h3>
                <h3>{props.quantity}</h3>
                <h3>{props.price}</h3>

                <IconButton onClick={handleAdd} color="primary" aria-label="add to shopping cart">
                    <Add/>
                </IconButton>
                <IconButton onClick={handleSubtract} aria-label="remove shopping cart" color="secondary">
                    <Remove/>
                </IconButton>
                <IconButton aria-label="delete" color="secondary">
                    <Delete/>
                </IconButton>
            </Box>

        </div>
    );
}
