import React, {useEffect} from "react";
import {RecordInCart} from "../RecordInCart";
import {Box, Button, Card, CardContent, makeStyles, Typography} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
    paperStyle: {
        minHeight: "20vh",
        width: "60vw",
        outlineColor: "blue",
        border: "darkslategray 4px solid",
        paddingTop: "0.5%",
        paddingBottom: "1vh",
        margin: "5vh 0vh 5vh 0vh",
    },
}));

class Product{
    id: number;
    name: string;
    quantity: number;
    price: number;

    constructor(id: number, name: string, quantity: number, price: number) {
        this.id = id;
        this.name = name;
        this.quantity = quantity;
        this.price = price;
    }
}

export const Cart: React.FC = props => {
    const [products, setProducts] = React.useState([] as Product[]);
    const [loading, setLoading] = React.useState(true);
    const classes = useStyles();

    useEffect(() => {
        let allCookies = document.cookie.split(";");
        let productsArr = [] as Product[];

        for (let cookie of allCookies){
            let id = cookie.split("=")[0]
            if (/[0-9]+/.test(id)){
                console.log("match = " + id)
                let vals = cookie.split("=")[1].split(",");

                productsArr.push(new Product(parseInt(id), vals[0],
                    parseInt(vals[1]), parseInt(vals[2])));
            }

        }

        console.log(productsArr);
        setProducts(productsArr);
        setLoading(false);
    }, []);

    return loading ? (<div>ładowanie</div>)
    : (
        <Box
            display="flex"
            alignSelf="center"
            alignItems="center"
            flexDirection="column"
            justifyContent="center"
            style={{minHeight: "90vh"}}
        >
            <Typography variant="h3" className="titleStyle">
                Twój Koszyk
            </Typography>

            <Card className={classes.paperStyle} variant="outlined">
                <Box style={{minHeight: "18vh"}}>
                    <RecordInCart />
                    <RecordInCart />
                </Box>

                <div style={{marginTop: "5%"}}>
                    <Button
                        variant="outlined"
                        color="secondary"
                        size="small"
                        style={{marginRight: "5%"}}
                        //component={Link}
                        //to="/"
                    >
                        Anuluj
                    </Button>

                    <Button variant="contained" size="medium" color="primary">
                        Kup
                    </Button>
                </div>
            </Card>
        </Box>
    );
}
