import React from "react";
import TextField from '@material-ui/core/TextField';
import { createStyles, makeStyles, Theme} from '@material-ui/core/styles';
import "./RegisterComponent.css";
import IconButton from '@material-ui/core/IconButton';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import FormControl from '@material-ui/core/FormControl';
import {Button} from "@material-ui/core";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            '& .MuiTextField-root': {
                margin: theme.spacing(1),
                width: '25ch',
            },
        },
    }),
);

interface State {
    password: string;
    showPassword: boolean;
    name: string;
    surname: string;
    email: string;
}

interface Error {
    username: string;
    email: string;
    surname: string;
    password: string;

}

export default function RegisterComponent() {
    const [values, setValues] = React.useState<State>({
        password: '',
        showPassword: false,
        name: '',
        surname: '',
        email: '',
    });

    const [state, setState] = React.useState(false);
    const [errors, setErrors] = React.useState<Error>({
        username: "",
        surname: "",
        email: "",
        password: "",
    });
    const classes = useStyles();

    const validateEmail = (value: any) => {
        const re =
            /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        setErrors({ ...errors, email: "" });
        if (value.length === 0) {
            setErrors({ ...errors, email: "Email jest wymagany." });
        } else if (!re.test(String(value).toLowerCase())) {
            setErrors({ ...errors, email: "Niepoprawny adres email." });
        } else if (value.length > 64) {
            setErrors({
                ...errors,
                email: "Email nie powinien mieć więcej niż 64 znaki.",
            });
        }
    };

    const validateName = (value: any) => {
        setErrors({ ...errors, username: "" });
        if (value.length === 0) {
            setErrors({ ...errors, username: "Imię użytkownika jest wymagane." });
        } else if (value.length > 64) {
            setErrors({
                ...errors,
                username: "Imię użytkownika nie powinno mieć więcej niż 64 znaki.",
            });
        }
    };

    const validateSurname = (value: any) => {
        setErrors({ ...errors, surname: "" });
        if (value.length === 0) {
            setErrors({ ...errors, surname: "Nazwisko użytkownika jest wymagane." });
        } else if (value.length > 64) {
            setErrors({
                ...errors,
                surname: "Nazwisko użytkownika nie powinno mieć więcej niż 64 znaki.",
            });
        }
    };

    const handleChange = (prop: keyof State) => (event: React.ChangeEvent<HTMLInputElement>) => {
        setValues({ ...values, [prop]: event.target.value });
    };

    const handleClickShowPassword = () => {
        setValues({ ...values, showPassword: !values.showPassword });
    };

    const handleMouseDownPassword = (event: React.MouseEvent<HTMLButtonElement>) => {
        event.preventDefault();
    };


    const handleName = (event: React.ChangeEvent<HTMLInputElement>) => {
        setValues({...values, name: event.target.value});
        validateName(event.target.value);

    }

    const handleSurname = (event: React.ChangeEvent<HTMLInputElement>) => {
        setValues({...values, surname: event.target.value});
        validateSurname(event.target.value);


    }

    const handleEmail = (event: React.ChangeEvent<HTMLInputElement>) => {
        setValues({...values, email: event.target.value});
        validateEmail(event.target.value);
    }

    const handleRegisterButton = (e: React.FormEvent) => {
        // e.preventDefault();
        console.log(values);
    }

    const validate = () => {
        validateSurname(values.surname);
        validateName(values.name);
        validateEmail(values.email);

        console.log("HALO?")
        console.log(errors)
    }

    return(
        <div className="div-container">
            <form className="div-container" onSubmit={handleRegisterButton}>
            <TextField
                onChange={handleName}
                className="input-field"
                label="Imię"
                required
                error={Boolean(errors?.username)}
                helperText={errors?.username}
                value={values.name}
                defaultValue={values.name}
            />
            <TextField
                onChange={handleSurname}
                className="input-field"
                label="Nazwisko"
                error={Boolean(errors?.surname)}
                helperText={errors?.surname}
                value={values.surname}
                required
                defaultValue={values.surname}
            />
            <TextField
                onChange={handleEmail}
                className="input-field"
                label="Email"
                error={Boolean(errors?.email)}
                helperText={errors?.email}
                required
                value={values.email}
                defaultValue={values.email}

            />
            <FormControl className="input-field" >
                <InputLabel required htmlFor="standard-adornment-password">Password</InputLabel>
                <Input
                    id="standard-adornment-password"
                    type={values.showPassword ? 'text' : 'password'}
                    value={values.password}
                    onChange={handleChange('password')}
                    endAdornment={
                        <InputAdornment position="end">
                            <IconButton
                                aria-label="toggle password visibility"
                                onClick={handleClickShowPassword}
                                onMouseDown={handleMouseDownPassword}
                            >
                                {values.showPassword ? <Visibility /> : <VisibilityOff />}
                            </IconButton>
                        </InputAdornment>
                    }
                />
            </FormControl>
            <Button type="submit" className="button" onClick={validate} variant="contained">Zarejestruj</Button>
            </form>
        </div>


    )
}
