import React from "react";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from '@material-ui/core/styles';
import CardActions from '@material-ui/core/CardActions';
import Button from '@material-ui/core/Button';
import "../Product.css"


export default function ProductCard(props: any){


    const handleAddToCart = () => {
        console.log("pyk")
        // items= id, name, quantity, price; id, name, quantity, price

        let allCookies = document.cookie.split(";");
        for (let cookie of allCookies){
            console.log(cookie)
            let id = cookie.split("=")[0]
            let vals = id[1].split(",");

            console.log("vals = " + vals)

            if (id == props.id){
                console.log("equal")
                document.cookie = `${props.id}=${props.name}, , ${props.price}; path=/`
            }
            else{
                document.cookie = `${props.id}=${props.name}, 1, ${props.price}; path=/`
            }
        }

    }

    return(
        <Card className="card" variant="outlined">
            <CardContent>
                <Typography variant="h5" component="h2" className="title">
                    {props.name}
                </Typography>
                <Typography className="pos" color="textSecondary">
                    {props.price} z≈Ç
                </Typography>
                <Typography variant="body2" component="p" className="description">
                    {props.description}
                </Typography>
            </CardContent>
            <CardActions>
                <Button onClick={handleAddToCart} variant="contained" color="primary" size="small">Dodaj do koszyka</Button>
            </CardActions>
        </Card>
    )
}
